mapper.select:
SELECT id, sector, title, content, file_id AS filedId, user_id AS userId, write_date AS writeDate, views, likes
FROM activity
WHERE id = #{id}

mapper.update:
UPDATE activity
SET title = #{title}, content = #{content}
WHERE id = #{id}

UPDATE activity
SET views = views + 1
WHERE id = #{id}

UPDATE activity
SET likes = likes + 1
WHERE id = #{postId} AND user_id = #{userId}

UPDATE activity
SET likes = likes - 1
WHERE id = #{postId} AND user_id = #{userId}
