mapper.select:
SELECT BOARD_ID AS boardId, TITLE, CONTENT, NAME, WRITE_DATE AS writeDate, VIEWS
FROM CLEAN_BOARD 
WHERE BOARD_ID = #{boardId}

mapper.update:
UPDATE CLEAN_BOARD
SET TITLE = #{title}, CONTENT = #{content}
WHERE BOARD_ID = #{boardId}

UPDATE CLEAN_BOARD
SET VIEWS = VIEWS + 1
WHERE BOARD_ID = #{boardId}
